mapper.select:
SELECT * FROM ELE_S_MENU bm WHERE bm.menu_id IN(SELECT rm.menu_id FROM ELE_S_USERROLE t LEFT JOIN ELE_S_USER u ON t.userid = u.userid LEFT JOIN ELE_S_ROLETOMENU rm ON t.role_id = rm.role_id WHERE u.login_name = #{login_name}
AND bm.parentid = #{parentid}
) ORDER BY bm.displayorder

